import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import './App.css'; // Assurez-vous d'inclure votre fichier CSS

const SignIn = () => {
  const [formData, setFormData] = useState({
    name: '',
    prename: '',
    username: '',
    email: '',
    password: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Ici, vous pouvez ajouter la logique pour traiter les données d'inscription
    console.log('Données d\'inscription soumises :', formData);
  };

  return (
    <div className="form-container">
      <h2>Inscription</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="name" placeholder="Nom" value={formData.name} onChange={handleChange} />
        <input type="text" name="prename" placeholder="Prénom" value={formData.prename} onChange={handleChange} />
        <input type="text" name="username" placeholder="Nom d'utilisateur" value={formData.username} onChange={handleChange} />
        <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} />
        <input type="password" name="password" placeholder="Mot de passe" value={formData.password} onChange={handleChange} />
        <button type="submit">S'inscrire</button>
      </form>
      <Link to="/login">Vous avez déjà un compte ? Se connecter</Link>
    </div>
  );
};

const Login = () => {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Ici, vous pouvez ajouter la logique pour traiter les données de connexion
    console.log('Données de connexion soumises :', formData);
  };

  return (
    <div className="form-container">
      <h2>Connexion</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="username" placeholder="Nom d'utilisateur" value={formData.username} onChange={handleChange} />
        <input type="password" name="password" placeholder="Mot de passe" value={formData.password} onChange={handleChange} />
        <button type="submit">Se connecter</button>
      </form>
      <Link to="/signin">Nouveau ici ? S'inscrire</Link>
    </div>
  );
};

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/signin" element={<SignIn />} />
        <Route path="/login" element={<Login />} />
        <Route path="/" element={<SignIn />} /> {/* Route par défaut vers la page d'inscription */}
      </Routes>
    </Router>
  );
}

export default App;
