import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';
import './App.css';
import axios from 'axios'; // Import axios for HTTP requests
import Header from './Header'; // Import the Header component
import Home from './Home'; // Import your Home component
import About from './About'; // Import your About component
import Contact from './Contact'; // Import your Contact component
import Reviews from './Reviews'; // Import your Reviews component

// Header component remains the same
const AppHeader = () => {
  return (
    <header>
      <div className="logo">
        {/* Place your university logo image here */}
        <img src="/path/to/your/logo.png" alt="University Logo" />
      </div>
      <nav>
        <ul>
          <li><Link to="/">Home</Link></li>
          <li><Link to="/about">About</Link></li>
          <li><Link to="/contact">Contact</Link></li>
        </ul>
      </nav>
    </header>
  );
};

// SignIn component for user registration
const SignIn = () => {
  const [formData, setFormData] = useState({
    name: '',
    prename: '',
    username: '',
    email: '',
    password: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Send POST request to register endpoint
      const response = await axios.post('http://localhost:3000/api/register', formData);
      console.log('User registered successfully:', response.data);
      // Redirect to login page after successful registration
      // Use Navigate component to navigate programmatically
      window.location = '/login';
    } catch (error) {
      console.error('Error registering user:', error);
    }
  };

  return (
    <div className="form-container">
      <h2>Inscription</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="name" placeholder="Nom" value={formData.name} onChange={handleChange} />
        <input type="text" name="prename" placeholder="Prénom" value={formData.prename} onChange={handleChange} />
        <input type="text" name="username" placeholder="Nom d'utilisateur" value={formData.username} onChange={handleChange} />
        <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} />
        <input type="password" name="password" placeholder="Mot de passe" value={formData.password} onChange={handleChange} />
        <button type="submit">S'inscrire</button>
      </form>
      <Link to="/login">Se connecter</Link>
    </div>
  );
};

// Login component for user login
const Login = () => {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Send POST request to login endpoint
      const response = await axios.post('http://localhost:3000/api/login', formData);
      console.log('Login successful:', response.data);
      // Handle login success (e.g., store token in local storage)
    } catch (error) {
      console.error('Error logging in:', error);
    }
  };

  return (
    <div className="form-container">
      <h2>Connexion</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="username" placeholder="Nom d'utilisateur" value={formData.username} onChange={handleChange} />
        <input type="password" name="password" placeholder="Mot de passe" value={formData.password} onChange={handleChange} />
        <button type="submit">Se connecter</button>
      </form>
      <Link to="/signin">S'inscrire</Link>
    </div>
  );
};

// App component containing routing setup
function App() {
  return (
    <Router>
      <div className="app">
        <AppHeader /> {/* Include the Header component */}
        <Routes>
          <Route path="/" element={<SignIn />} />
          <Route path="/signin" element={<SignIn />} />
          <Route path="/login" element={<Login />} />
          <Route path="/about" element={<About />} />
          <Route path="/contact" element={<Contact />} />
          <Route path="/reviews" element={<Reviews />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
